import React from 'react';
import { Link } from 'react-router-dom';

const Home = ()=> {
  return (
    <section className="relative bg-cover bg-center" style={{backgroundImage: "url('https://images.unsplash.com/photo-1503387762-592deb58ef4e?q=80&w=1600&auto=format&fit=crop')"}}>
      <div className="bg-gray-900/60 text-white py-36">
        <div className="max-w-6xl mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-extrabold">We Turn Blueprints into Landmarks</h1>
          <p className="mt-4 text-lg max-w-2xl mx-auto">On-time, on-budget builds with zero compromise on safety and quality.</p>
          <div className="mt-8 flex justify-center gap-4">
            <Link to="/booking" className="bg-amber-500 px-6 py-3 rounded-md font-semibold">Book Appointment</Link>
            <Link to="/projects" className="bg-white/10 px-6 py-3 rounded-md">View Projects</Link>
          </div>
        </div>
      </div>
      <section className="py-16">
        <div className="max-w-6xl mx-auto px-4 grid md:grid-cols-3 gap-6">
          <div className="bg-white rounded-lg p-6 shadow"> <h3 className="font-bold">Residential</h3><p className="mt-2 text-sm text-gray-600">Custom homes and extensions.</p></div>
          <div className="bg-white rounded-lg p-6 shadow"> <h3 className="font-bold">Commercial</h3><p className="mt-2 text-sm text-gray-600">Office, retail and industrial builds.</p></div>
          <div className="bg-white rounded-lg p-6 shadow"> <h3 className="font-bold">Renovations</h3><p className="mt-2 text-sm text-gray-600">Fit-outs and upgrades with minimal downtime.</p></div>
        </div>
      </section>
    </section>
  );
};

export default Home;
