import React, { useState } from 'react';
const images = [
  { src: 'https://images.unsplash.com/photo-1505691723518-36a5ac3b2b8f', alt:'Project 1' },
  { src: 'https://images.unsplash.com/photo-1497366216548-37526070297c', alt:'Project 2' },
  { src: 'https://images.unsplash.com/photo-1521737604893-d14cc237f11d', alt:'Project 3' },
  { src: 'https://images.unsplash.com/photo-1512917774080-9991f1c4c750', alt:'Project 4' },
  { src: 'https://images.unsplash.com/photo-1520607162513-77705c0f0d4a', alt:'Project 5' }
];

const Gallery = ()=> {
  const [open, setOpen] = useState(false);
  const [idx, setIdx] = useState(0);
  return (
    <section className="py-16">
      <div className="max-w-6xl mx-auto px-4">
        <h2 className="text-3xl font-bold mb-6">Portfolio</h2>
        <div className="grid md:grid-cols-3 gap-4">
          {images.map((img, i)=> (
            <button key={i} onClick={()=>{ setIdx(i); setOpen(true); }} className="overflow-hidden rounded-lg">
              <img src={img.src + '?q=80&w=800&auto=format&fit=crop'} alt={img.alt} className="w-full h-56 object-cover hover:scale-105 transition" />
            </button>
          ))}
        </div>
        {open && (
          <div className="lightbox" onClick={()=>setOpen(false)}>
            <img src={images[idx].src + '?q=80&w=1400&auto=format&fit=crop'} alt={images[idx].alt} />
          </div>
        )}
      </div>
    </section>
  );
};

export default Gallery;
