import React from 'react';

const Careers = ()=> {
  const jobs = [
    { id:1, title:'Site Engineer', location:'Nairobi' },
    { id:2, title:'Project Manager', location:'Mombasa' },
    { id:3, title:'Civil Technician', location:'Kisumu' }
  ];
  return (
    <section className="py-20">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold mb-6">Careers / Recruitment</h1>
        <div className="grid md:grid-cols-3 gap-6">
          {jobs.map(job=>(
            <div key={job.id} className="p-6 bg-white rounded-lg shadow">
              <h3 className="font-semibold">{job.title}</h3>
              <p className="text-gray-600">{job.location}</p>
              <button className="mt-4 border border-amber-500 text-amber-500 px-4 py-2 rounded">Apply</button>
            </div>
          ))}
        </div>
        <div className="mt-10 bg-white p-6 rounded shadow">
          <h3 className="font-semibold">Apply by Email</h3>
          <p className="text-sm text-gray-600 mt-2">Send CV and cover letter to careers@kembove.co.ke</p>
        </div>
      </div>
    </section>
  );
};

export default Careers;
