import React from 'react';

const Products = ()=> {
  const products = [
    { id:1, name:'Cement (50kg)', price:'KSh 800' },
    { id:2, name:'Bricks (per 1000)', price:'KSh 20,000' },
    { id:3, name:'Steel Rod (12mm)', price:'KSh 250/m' }
  ];
  return (
    <section className="py-20">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold mb-6">Products / Shop</h1>
        <div className="grid md:grid-cols-3 gap-6">
          {products.map(p=>(
            <div key={p.id} className="p-6 bg-white rounded-lg shadow">
              <h3 className="font-semibold">{p.name}</h3>
              <p className="text-gray-600 mt-2">{p.price}</p>
              <button className="mt-4 bg-amber-500 text-white px-4 py-2 rounded">Enquire</button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Products;
