import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { X, ChevronLeft, ChevronRight } from "lucide-react";

const images = [
  { 
    src: "https://images.unsplash.com/photo-1505691723518-36a5ac3b2b8f", 
    alt: "Project 1", 
    description: "Modern Office Workspace Design" 
  },
  { 
    src: "https://images.unsplash.com/photo-1497366216548-37526070297c", 
    alt: "Project 2", 
    description: "Team Collaboration in Action" 
  },
  { 
    src: "https://images.unsplash.com/photo-1521737604893-d14cc237f11d", 
    alt: "Project 3", 
    description: "Creative Project Planning" 
  },
  { 
    src: "https://images.unsplash.com/photo-1512917774080-9991f1c4c750", 
    alt: "Project 4", 
    description: "City Architecture Concept" 
  },
  { 
    src: "https://images.unsplash.com/photo-1520607162513-77705c0f0d4a", 
    alt: "Project 5", 
    description: "Photography Showcase" 
  },
];

const Gallery = () => {
  const [open, setOpen] = useState(false);
  const [idx, setIdx] = useState(0);

  const handleNext = (e) => {
    e.stopPropagation();
    setIdx((prev) => (prev + 1) % images.length);
  };

  const handlePrev = (e) => {
    e.stopPropagation();
    setIdx((prev) => (prev - 1 + images.length) % images.length);
  };

  return (
    <section className="py-16 bg-gray-50">
      <div className="max-w-6xl mx-auto px-4">
        <h2 className="text-3xl font-bold mb-10 text-center">Our Projects</h2>
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {images.map((img, i) => (
            <motion.button
              key={i}
              onClick={() => {
                setIdx(i);
                setOpen(true);
              }}
              className="group relative overflow-hidden rounded-2xl shadow-md"
              whileHover={{ scale: 1.03 }}
              transition={{ duration: 0.3 }}
            >
              <img
                src={img.src + "?q=80&w=800&auto=format&fit=crop"}
                alt={img.alt}
                className="w-full h-64 object-cover"
              />
              <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 flex items-center justify-center text-white text-lg font-semibold transition">
                {img.alt}
              </div>
            </motion.button>
          ))}
        </div>

        {/* Lightbox */}
        <AnimatePresence>
          {open && (
            <motion.div
              className="fixed inset-0 bg-black/80 flex items-center justify-center z-50"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setOpen(false)}
            >
              <div className="relative max-w-4xl w-full px-6">
                <motion.img
                  key={idx}
                  src={images[idx].src + "?q=80&w=1400&auto=format&fit=crop"}
                  alt={images[idx].alt}
                  className="rounded-2xl shadow-lg max-h-[80vh] mx-auto"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  exit={{ scale: 0.8, opacity: 0 }}
                  transition={{ duration: 0.3 }}
                />
                <p className="text-center text-white mt-4 text-lg">
                  {images[idx].description}
                </p>

                {/* Controls */}
                <button
                  onClick={handlePrev}
                  className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/20 p-3 rounded-full hover:bg-white/40"
                >
                  <ChevronLeft className="text-white" size={28} />
                </button>
                <button
                  onClick={handleNext}
                  className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/20 p-3 rounded-full hover:bg-white/40"
                >
                  <ChevronRight className="text-white" size={28} />
                </button>
                <button
                  onClick={() => setOpen(false)}
                  className="absolute top-4 right-6 bg-white/20 p-2 rounded-full hover:bg-white/40"
                >
                  <X className="text-white" size={24} />
                </button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </section>
  );
};

export default Gallery;
